mapper.select:
SELECT tl.id as id, tl.entity_id as entityId, tl.entity_name as entityName, tl.entity_field as entityField , CASE WHEN tl.reply_yn = 'Y' THEN true WHEN tl.reply_yn = 'N' THEN false END as replyYn , tl.persist_type as persistType, tl.old_value as oldValue, tl.new_value as newValue , tl.etc_value as etcValue, tl.attached_file_id as attachedFileId, tl.task_id as taskId, t.name as taskName, tl.project_id as projectId , tl.created_by as createdBy, tl.created_date as createdDate, tl.last_modified_by as lastModifiedBy, tl.last_modified_date as lastModifiedDate , u.profile_image_id as profileImageId, u.name as createdByName, u.id as createdById , af.id as replyAttachedFileId, af.name as attachedFileName , af.size as attachedFileSize, af.content_type as attachedFileContentType FROM owl_trace_log tl LEFT OUTER JOIN owl_task t ON tl.task_id = t.id LEFT OUTER JOIN owl_trace_log_attached_file tlaf ON tlaf.trace_log_id = tl.id LEFT JOIN owl_attached_file af ON tlaf.attached_file_id = af.id LEFT JOIN owl_user u ON tl.created_by = u.login WHERE 1=1
AND tl.task_id = #{entityId}
AND tl.project_id = #{entityId}
AND tl.entity_id = #{entityId}
AND tl.entity_field = #{entityField}
AND NOT EXISTS ( SELECT itlaf.id FROM owl_trace_log_attached_file itlaf WHERE itlaf.trace_log_id = tl.id )
ORDER BY tl.id ASC
